import React, { useState, useEffect } from 'react';
import { Container, Segment, Header, Message, List, Icon } from 'semantic-ui-react';
import { useAuth } from '../context/AuthContext';
import { backApi } from '../misc/BackApi';
import { handleLogError } from '../misc/Helpers';

const TransactionsList = () => {
    const { getUser } = useAuth();
    const [transactions, setTransactions] = useState([]);
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(true);
    const [currentUser, setCurrentUser] = useState(null);

    useEffect(() => {
        const fetchUsernames = async (transactions) => {
            const userRequests = transactions.map(transaction => (
                Promise.all([
                    backApi.getUserById(transaction.fromWalletId),
                    backApi.getUserById(transaction.toWalletId)
                ]).then(([fromUserResponse, toUserResponse]) => ({
                    ...transaction,
                    fromUsername: fromUserResponse.data.username,
                    toUsername: toUserResponse.data.username
                }))
            ));

            return Promise.all(userRequests);
        };

        const fetchTransactions = async () => {
            try {
                const user = getUser();
                if (user && user.data) {
                    setCurrentUser(user.data);
                    const response = await backApi.getAllTransactions(user);
                    const transactionsWithUsernames = await fetchUsernames(response.data);
                    setTransactions(transactionsWithUsernames);
                } else {
                    throw new Error("User data not found");
                }
            } catch (error) {
                handleLogError(error);
                setError(error.message);
            } finally {
                setLoading(false);
            }
        };

        fetchTransactions();
    }, [getUser]);

    if (loading) return <Message>Loading...</Message>;
    if (error) return <Message negative>Error: {error}</Message>;

    return (
        <Container>
            <Segment>
                <Header as="h3">All Transactions</Header>
                {transactions.length === 0 ? (
                    <Message>No transactions found.</Message>
                ) : (
                    <List divided relaxed>
                        {transactions.map(transaction => {
                            const isSender = transaction.fromWalletId === currentUser.walletId;  // Assuming currentUser has walletId property
                            const iconColor = isSender ? 'red' : 'green';
                            const transactionType = isSender ? 'Sent' : 'Received';
                            const userLabel = isSender ? `To: ${transaction.toUsername}` : `From: ${transaction.fromUsername}`;

                            return (
                                <List.Item key={transaction.id}>
                                    <List.Content>
                                        <List.Header>
                                            <Icon name='exchange' color={iconColor} /> {transactionType}
                                        </List.Header>
                                        <List.Description>
                                            {userLabel}
                                        </List.Description>
                                        <List.Description>
                                            Amount: {transaction.amount} {transaction.currency}
                                        </List.Description>
                                        <List.Description>
                                            Type: {transaction.transactionType}
                                        </List.Description>
                                        {/* Date field commented out as it's missing in the provided data */}
                                        {/* <List.Description>
                                            Date: {new Date(transaction.timestamp).toLocaleString()}
                                        </List.Description> */}
                                    </List.Content>
                                </List.Item>
                            );
                        })}
                    </List>
                )}
            </Segment>
        </Container>
    );
};

export default TransactionsList;
